<?php


namespace Nemundo\Content\Index\Group\Com\Container;


use Nemundo\Admin\Com\Table\AdminTable;
use Nemundo\Admin\Com\Title\AdminSubtitle;
use Nemundo\Com\TableBuilder\TableRow;
use Nemundo\Content\Com\Container\AbstractContentTypeContainer;
use Nemundo\Content\Index\Group\Type\GroupIndexTrait;
use Nemundo\Html\Container\AbstractContainer;

class GroupContainer extends AbstractContentTypeContainer
{

    public function getContent()
    {

        if ($this->contentType->isObjectOfTrait(GroupIndexTrait::class)) {


            $subtitle = new AdminSubtitle($this);
            $subtitle->content = 'Group';

            $table = new AdminTable($this);

            $header = new TableRow($table);
            $header->addText('Group');

            foreach ($this->contentType->getUserList() as $userRow) {
                $row = new TableRow($table);
                $row->addText($userRow->login);
            }

        }

        return parent::getContent(); // TODO: Change the autogenerated stub
    }

}