<?php

namespace Nemundo\App\System\Com\Table;


use Nemundo\Admin\Com\Table\AdminLabelValueTable;
use Nemundo\App\Mail\MailConfig;
use Nemundo\Core\System\PhpEnvironment;
use Nemundo\Core\Type\DateTime\DateTime;

class MailTable extends AdminLabelValueTable
{

    public function getContent()
    {

        $this->addLabelValue('Host', MailConfig::$mailConnection->host);
        $this->addLabelValue('User', MailConfig::$mailConnection->user);
        $this->addLabelValue('Password', '***');
        $this->addLabelValue('Port', MailConfig::$mailConnection->port);

        $this->addLabelValue('Default From', MailConfig::$defaultMailFrom);
        $this->addLabelValue('Default From Text', MailConfig::$defaultMailFromText);


        return parent::getContent(); // TODO: Change the autogenerated stub
    }

}